package ifcondition;

public class ArrayClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] numbers = new int[5];
		numbers[0] = 110;
		numbers[1] = 22;
		numbers[2] = 3;
		numbers[3] = 77;
		numbers[4] = 9;

// find out the element of 4th index position
		System.out.println(numbers[4]);

		int[] x = new int[5];
		x[0] = 110;
		x[1] = 22;
		x[2] = 3;
		x[3] = 77;
		x[4] = 9;
		for (int a : x) {
			System.out.println(a);

			// Another way to careate Array

		}

	}

}
